import React, {Component} from 'react';
import './App.css';
import InfiniteScroll from 'react-infinite-scroller';
import axios from 'axios';



class App extends Component {
	constructor(props){
		super(props);
		this.state={
			articles: [],
			hasMoreItems: true,
			page:1,
			filter:""
		};
  }

	componentDidMount(){
		var newnews = [];
		console.log(this.state.page);

		var p = this.state.page;
		var f = this.state.filter;
		newnews = this.News(p,f);

		this.setState({
			articles: this.state.articles.concat(newnews)
		});
		console.log(newnews);

		if(this.state.page<10) {
			this.setState({
				page: this.state.page+1
			});
		} else {
			this.setState({
				hasMoreItems: false
			})};

	}
	News (x,filter){
		var news= [];
		var URL;
		var word = filter.toLowerCase();
		if (filter==null)
	  {
		  URL='https://newsapi.org/v2/sources=the-washington-post&pageSize=10&page='+x+'&apiKey=24dc597ec27f40729ac17a7231403638';
	  }
	  else{
		URL='https://newsapi.org/v2/everything?q='+word+'sources=the-washington-post&pageSize=10&page='+x+'&apiKey=24dc597ec27f40729ac17a7231403638';
	  }
		axios.get(URL)
			.then(({data})=>{
				news= data.articles;
			  })
		console.log(news);
		return news;
	  }

	handleChange = event => {
		this.setState({ filter: event.target.value });
	};
  
	render(){
		console.log(this.state);
		const loader = <div className="loader">Loading ...</div>;
		return (
			
			<div className="App">
				<div className="header">
					<h2> US News </h2>
					<input value={this.state.filter} onChange={this.handleChange} />
				</div>

				
				<InfiniteScroll
                pageStart={0}
                loadMore={this.componentDidMount}
				hasMore={this.state.hasMoreItems}
                loader={loader}>

				<div className="row">
				{this.state.articles.map((item, index)=>{
					return(
						
							<div className="card" key={index}>
							<div>
								<img className="image" src={item.urlToImage}/>
								<h3 className='link'>
									<a href={item.url} target="_blank" >
										{item.title}
									</a>
								</h3>
								<p className='publishedAt'>{item.publishedAt}</p>
								<p className="description">{item.description} </p>
							</div>
							</div>
						)
					
				})}
			</div>	
				</InfiniteScroll>
				
			</div>
		);
	}
}
export default App;
